  triggers: {
    onPOS: {
      type: Boolean,
      default: true
    },
    onWidget: {
      type: Boolean,
      default: true
    }
  },
  isDelete: {
    type: Boolean,
    default: false
  }
}, { timestamps: true, skipVersioning: true })

export default tenantModel('RepairCategory', repairCategorySchema)

