    const stock = await _InventoryStockModel().findOneAndUpdate({ _id: data.inventoryStock }, { $set: flatten(inventoryStock) }, { new: true })
    data.inventoryStock = stock
    return res.json({ data })
  } catch (err) {
    console.log(err)
