      jobId: newJobNumber,
      title: 'From Estimate',
      description: 'Estimate converted to Job',
      client,
      serviceAddress: { ...propertyAddress },
      storeId,
      expenses
    })

    return res.json({ data })
  } catch (err) {
    console.log(err)
    return res.status(500).json({ error: true, reason: err.message })
  }
}

